import 'package:flutter/material.dart';

class Assignment extends StatefulWidget {
  const Assignment({super.key});

  @override
  State<Assignment> createState() => _ListView();
}

class _ListView extends State<Assignment> {
  List<String> imageList = [
    "data:image/jpeg;base64,",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQMigbMzYohHG5Bu1zuIVDR2DDf2w2Y9Kegrt5za-Te0atCHInSLaCnBIgbrw&s",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ_e9CiQPy0ZFTTaL8mCxrczQC_EoVvxoSknNBC5surhCh4EGngm5yDS8M8ag&s",
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        centerTitle: true,
        title: const Text("ListView Demo"),
        backgroundColor: Colors.amber,
      ),
      body: ListView.builder(
          itemCount: imageList.length,
          itemBuilder: (context, index) {
            return Container(
              margin: const EdgeInsets.all(20),
              child: Image.network(imageList[index]),
            );
          }),
    );
  }
}
